import React, { useState } from "react";
import TextField from '@material-ui/core/TextField';
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import { Button } from "@material-ui/core";
import './ReachOut.css';

const ReachOut = () => {
    const [value, setValue] = React.useState('');
    const handleChange = (event) => {
        setValue(event.target.value);
    };

    const[firstName, setFirstName] = useState("");
    const[lastName, setLastName] = useState("");
    const[email, setEmail] = useState("");
    const[message, setMessage] = useState("");
    const[firstNameError, setFirstNameError] = useState("");
    const[lastNameError, setLastNameError] = useState("");
    const[emailError, setEmailError] = useState("");
    const[messageError, setMessageError] = useState("");

    const Validate = () => {
        let tempFirstError = "";
        let tempLastError = "";
        let tempEmailError = "";
        let tempMessageError = "";

        if(firstName === "") {
            tempFirstError = "Field Empty";
        }
        if(lastName === "") {
            tempLastError = "Field Empty";
        }
        if(email === "") {
            tempEmailError = "Invalid Email";
        }
        if(message === "") {
            tempMessageError = "Field Empty";
        }

        if(!firstName || !lastName || !email || !message || !email.includes('@')) {
            setFirstNameError(tempFirstError);
            setLastNameError(tempLastError);
            setEmailError(tempEmailError);
            setMessageError(tempMessageError);
            return false;
        }
        return true;
    }

    const onSubmit = () => {
        if(Validate()) {
            console.log("Success");
        } else {
            console.log("Error");
        }
    }
    
    return (
        <>
            <p className = "finalComponent">Hello, thanks a lot for taking the time out to read my blog and for trying to reach out to me! I'm very grateful for that :)</p>
            <MuiThemeProvider>
                <div style = {{margin: "50px auto", maxWidth: "500px"}}>
                    <TextField
                        label = "FirstName"
                        placeholder = "Please enter your first name."
                        helperText = {firstNameError}
                        value = {firstName}
                        onChange = {(event) => {setFirstName(event.target.value)}}
                        fullWidth = "true"
                        variant="outlined"
                    />
                    <br />
                    <br />
                    <TextField
                        label = "LastName"
                        placeholder = "Please enter your last name."
                        value = {lastName}
                        onChange = {(event) => {setLastName(event.target.value)}}
                        fullWidth = "true"
                        variant="outlined"
                    />
                    <br />
                    <br />
                    <TextField
                        label = "Email"
                        placeholder = "Please enter your email ID."
                        value = {email}
                        onChange = {(event) => {setEmail(event.target.value)}}
                        fullWidth = "true"
                        variant="outlined"
                    />
                    <br />
                    <br />
                    <TextField
                        label="Message"
                        placeholder = "I'd love to hear from you!"
                        value = {message}
                        onChange = {(event) => {setMessage(event.target.value)}}
                        fullWidth = "true"
                        multiline
                        maxRows={4}
                        value={value}
                        onChange={handleChange}
                        variant="outlined"
                    />
                    <br />
                    <br />
                    <Button color = "primary" variant = "contained" onClick = {onSubmit}>Submit</Button>
                </div>
            </MuiThemeProvider>
        </>
    )
};
    
export default ReachOut;
