import React, { useState } from "react";
import TopBanner from "../../components/TopBanner";
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import { TextField } from "@material-ui/core";
import { Button } from "@material-ui/core";
import { state } from "react";
import './ReachOut.css';

const initialState = {
    firstName: "",
    lastName: "",
    email: "",
    message: "",
    firstNameError: "",
    lastNameError: "",
    emailError: "",
    messageError: ""
};

export default class ReachOut extends React.Component {
    state = initialState;
    
    handleChange = event => {
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
            ? event.target.checked
            : event.target.value
        });
    };

    validate = () => {
        let firstNameError = "";
        let lastNameError = "";
        let emailError = "";
        let messageError = "";
    
        if (!this.state.firstName) {
          firstNameError = "This field cannot be blank.";
        }
    
        if (!this.state.lastName) {
          lastNameError = "This field cannot be blank.";
        }
    
        if (!this.state.email.includes("@")) {
          emailError = "Invalid Email";
        }
    
        if (!this.state.message) {
          messageError = "This field cannot be blank.";
        }
    
        if (emailError || firstNameError || lastNameError || messageError) {
          this.setState({ emailError, firstNameError, lastNameError, messageError });
          return false;
        }
        return true;
      };
    
      handleSubmit = event => {
        event.preventDefault();
        const isValid = this.validate();
        if (isValid) {
          console.log(this.state);
          // clear form
          this.setState(initialState);
        }
      };

    render() {
    return (
        <>
            <p className = "finalComponent">Hello, thanks a lot for taking the time out to read my blog and for trying to reach out to me! I'm very grateful for that :)</p>
            <MuiThemeProvider>
                <form onSubmit={this.handleSubmit}>
                <div style = {{margin: "50px auto", maxWidth: "500px"}}>
                    <div>
                    <TextField
                    label = "FirstName"
                    placeholder = "Please enter your first name."
                    value={this.state.firstName}
                    onChange={this.handleChange}
                    fullWidth = "true"
                    variant="outlined"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>{this.state.firstNameError}</div>
                    </div>
                    <br />
                    <br />
                    <div>
                    <TextField
                        label = "LastName"
                        placeholder = "Please enter your last name."
                        value = {this.state.lastName}
                        onChange = {this.handleChange}
                        fullWidth = "true"
                        variant="outlined"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>{this.state.lastNameError}</div>
                    </div>
                    <br />
                    <br />
                    <div>
                    <TextField
                        label = "Email"
                        placeholder = "Please enter your email ID."
                        value = {this.state.email}
                        onChange = {this.handleChange}
                        fullWidth = "true"
                        variant="outlined"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>{this.state.emailError}</div>
                    </div>
                    <br />
                    <br />
                    <div>
                    <TextField
                        id="outlined-multiline-flexible"
                        label="Message"
                        placeholder = "I'd love to hear from you!"
                        fullWidth = "true"
                        multiline
                        maxRows={4}
                        value={this.state.message}
                        onChange={this.handleChange}
                        variant="outlined"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>{this.state.messageError}</div>
                    </div>
                    <br />
                    <br />
                    <Button color = "primary" variant = "contained">Submit</Button>
                </div>
                </form>
            </MuiThemeProvider>
        </>
    )
    }
};
